import React from "react";
import Button from "react-bootstrap/Button";
import Container from "react-bootstrap/Container";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import YoutubeVideo from "../sub-components/YoutubeVideo";
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from 'react-responsive-carousel';
import FlavorsData from "../sub-components/FlavorsData";

const Flavors = (props) => {
    return (
        <div id="flavors">
            <YoutubeVideo src={"https://www.youtube.com/embed/u0WV5bPSRf8"} />

            <div className="section">
                <Container>
                    {
                        FlavorsData.map(item => {
                            return (
                                <div className="section-inner" key={item.id}>
                                    <Row className="justify-content-center">
                                        <Col md="6">

                                                <Carousel
                                                    autoPlay={true}
                                                    interval={5000}
                                                    showIndicators={false}
                                                    showArrows={false}
                                                    showStatus={false}
                                                    infiniteLoop={true}
                                                    className={"w-50"}
                                                >
                                                    <img
                                                        src={item.gifImgURL}
                                                        alt={item.title}
                                                    />

                                                    <img
                                                        src={item.bucketImgURL}
                                                        alt={item.title}
                                                    />

                                                    <img
                                                        src="images/nutri1.png"
                                                        alt={item.title}
                                                    />
                                                </Carousel>
                                        </Col>

                                        <Col md="6">
                                            <div className="section-inner">
                                                <img src={item.headingImgURL} alt={item.title} />
                                            </div>

                                            <div className="section-inner">
                                                <h3>{item.description}</h3>
                                            </div>

                                            <div id="contains" className="section-inner">
                                                <ul>
                                                    {
                                                        item.keyPoints.map(keyPoint => {
                                                            return (
                                                                <li key={item.id}>{keyPoint}</li>
                                                            );
                                                        })
                                                    }
                                                </ul>
                                            </div>

                                            <div className="section-inner">
                                                <p>Ingredients: {item.ingredients}</p>
                                            </div>

                                            <div className="section-inner">
                                                <p>Contains: {item.contains}</p>
                                            </div>

                                            <div className="section-inner">
                                                <p>{item.note}</p>
                                            </div>

                                            <div className="section-inner">
                                                <img className="w-25 mt-4" src="images/dairy.jpg" alt="Dairy" />
                                            </div>

                                            <div className="section-inner mt-4">
                                                <Button variant="primary" href="/store">Where to Buy</Button>
                                            </div>
                                        </Col>
                                    </Row>

                                    <hr />
                                </div>
                            );
                        })
                    }


                </Container>
            </div>
        </div>
    );
};


export default Flavors;
