import React from "react";
import Container from "react-bootstrap/Container";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import YoutubeVideo from "../sub-components/YoutubeVideo";

const Flavors = (props) => {
    const [flavorsData, setFlavorsData] = React.useState([]);
    const [carouselData, setCarouselData] = React.useState([]);

    React.useEffect(() => {
        const fetchFlavors = async () => {
            const response = await fetch("/flavors");
            const data = await response.json();

            console.log(data);

            const flavors = [];
            const carousels = [];


            data.forEach(item => {
                const carouselItems = [];

                carouselItems.push({
                    key: 1,
                    src: `images/flavors/${item.img}`,
                    alt: "Flavor Image"
                });

                carouselItems.push({
                    key: 2,
                    src: `images/flavors/${item.gif}`,
                    alt: "Flavor Gif"
                });

                carouselItems.push({
                    key: 3,
                    src: "images/nutri1.png",
                    alt: "Nutrition Value"
                });

                const flavorItem = {
                    key: item._id,
                    name: item.name,
                    description: "Unbelievable on its own, Cream is the foundation of all our flavours. Rich and not too sweet, it tastes amazing with both sweet and savoury pairings. Try it with a spoonful of olive oil or espresso on top…you’ll thank us later."
                }

                flavors.push(flavorItem);
                carousels.push(carouselItems);

            });

            console.log(flavors);
            console.log(carousels);

            setFlavorsData(flavors);

        };

        fetchFlavors();
    }, []);

    return (
        <div id="flavors">
            <YoutubeVideo src={"https://www.youtube.com/embed/u0WV5bPSRf8"} />

            <div className="section">
                <Container>
                    {
                        flavorsData.map(flavor => {
                            return (
                                <Row key={flavor.key}>
                                    <Col md="6">
                                    <Carousel
                            autoPlay={true}
                            interval={5000}
                            showIndicators={false}
                            showArrows={false}
                            showStatus={false}
                            infiniteLoop={true}>
                            {
                                carouselData.map(carouselItem => {
                                    return (
                                        <img
                                            className="w-50"
                                            key={carouselItem.key}
                                            src={carouselItem.src}
                                            alt={carouselItem.alt}
                                        />
                                    );
                                })
                            }
                        </Carousel>

                                    </Col>

                                    <Col md="6">
                                        <h1>{flavor.name}</h1>
                                        <h3>{flavor.description}</h3>
                                    </Col>
                                </Row>
                            );
                        })
                    }


                </Container>
            </div>
        </div>
    );
};


export default Flavors;
