import React from "react";
import Button from "react-bootstrap/Button";
import Container from "react-bootstrap/Container";
import CarouselBrands from "../sub-components/CarouselBrands";

const Home = (props) => {
    // const [brands, setBrands] = React.useState([]);
    const [carouselData, setCarouselData] = React.useState([]);

    React.useEffect(() => {

        const fetchBrands = async () => {
            const response = await fetch("/brands");
            const data = await response.json();

            const items = [];

            console.log(data);
            console.log(items);

            data.forEach(item => {
                const newItem = {
                    key: String,
                    src: String,
                    alt: String
                };

                newItem.key = item._id;
                newItem.src = `images/brands/${item.img}`;
                newItem.alt = item.name;

                items.push(newItem);
            });

            setCarouselData(items);

            console.log("new items: ", carouselData);
        }

        fetchBrands();
    }, []);

    return (
        <div id="home">
            <img className="w-100" src="/images/hotline.jpg" alt="Hotline" />

            <div id="carousel" className="section text-center">
