import React from "react";
import Container from "react-bootstrap/Container";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import YoutubeVideo from "../sub-components/YoutubeVideo";

const Flavors = (props) => {
    const [flavorsData, setFlavorsData] = React.useState([]);
    const [carouselData, setCarouselData] = React.useState([]);

    React.useEffect(() => {
        const fetchFlavors = async () => {
            const response = await fetch("/flavors");
            const data = await response.json();

            console.log(data);

            const carouselItem = {
                key: Number,
                src: String,
                alt: String
            };
            const flavors = [];


            data.forEach(item => {
                const carouselArray = [];

                carouselItem = {
                    key: 1,
                    src: `images/flavors/${item.img}`,
                    alt: "Flavor Image"
                };
                carouselArray.push(carouselItem);

                carouselItem = {
                    key: 2,
                    src: `images/flavors/${item.gif}`,
                    alt: "Flavor Gif"
                };
                carouselArray.push(carouselItem);

                const flavorItem = {
                    name: item.name,
                    description: "Unbelievable on its own, Cream is the foundation of all our flavours. Rich and not too sweet, it tastes amazing with both sweet and savoury pairings. Try it with a spoonful of olive oil or espresso on top…you’ll thank us later.",
                    carousel: carouselArray
                }

                flavors.push(flavorItem);



            });

            console.log(flavors);

            setCarouselData(flavors);

        };

        fetchFlavors();
    }, []);

    return (
        <div id="flavors">
            <YoutubeVideo src={"https://www.youtube.com/embed/u0WV5bPSRf8"} />

            <div className="section">
                <Container>
                    <Row>
                        <Col md="6">

                        </Col>

                        <Col md="6">

                        </Col>
                    </Row>

                </Container>
            </div>
        </div>
    );
};


export default Flavors;
