import React from "react";
import Carousel from "react-bootstrap/Carousel";
import Brands from "../../data/BrandsData"
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";


const CarouselBrands = () => {

    const [activeId, setActiveId] = React.useState(0);

    const handleClick = (e) => {

        const active = e.target.id-1;
        console.log(active);
        setActiveId(active);
    }

    slideCarousel(() => {
        const active = (activeId+1)%10;
        console.log(active);
        setActiveId(active);
    }, 5000);

    // setInterval(() => {
    //     const active = (activeId+1)%10;
    //     console.log(active);
    //     setActiveId(active);
    // }, 5000);

    return (
        <div>
            <Carousel activeIndex={activeId} interval="5000">
                {
                    Brands.map(brand => {
                        return (
                            <Carousel.Item>
                                <img
                                className="w100"
                                src={brand.src}
                                alt={brand.title}
                                />
                            </Carousel.Item>
                        );
                    })   
                }
            </Carousel>

            <Row>
                <Col>
                {
                    Brands.map(brand => {
                        return (
                            <img className="indicator-img"
                            id={brand.id}
                            src={brand.src}
                            alt={brand.title}
                            onClick={handleClick}
                            />
                        );
                    })
                }

                </Col>
            </Row>
        </div>
    );
};


export default CarouselBrands;
