import React from "react";
import Container from "react-bootstrap/Container";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import YoutubeVideo from "../sub-components/YoutubeVideo";

const Flavors = (props) => {
    const [flavorsData, setFlavorsData] = React.useState([]);
    const [carouselData, setCarouselData] = React.useState([]);

    React.useEffect(() => {
        const fetchFlavors = async () => {
            const response = await fetch("/flavors");
            const data = await response.json();

            console.log(data);


            const flavors = [];


            data.forEach(item => {
                const carouselArray = [];

                const carouselItem = {
                    key: item._id,
                    src: `images/flavors/${item.img}`,
                    alt: item.name
                };

                carouselArray.push(carouselItem);

                const flavorItem = {
                    name: item.name,
                    description: "Some description",
                    carousel: carouselArray
                }

                flavors.push(flavorItem);

            });

            setCarouselData(flavors);

        };

        fetchFlavors();
    }, []);

    return (
        <div id="flavors">
            <YoutubeVideo src={"https://www.youtube.com/embed/u0WV5bPSRf8"} />

            <div className="section">
                <Container>
                    <Row>
                        <Col md="6">

                        </Col>

                        <Col md="6">

                        </Col>
                    </Row>

                </Container>
            </div>
        </div>
    );
};


export default Flavors;
