import React from "react";
import Container from "react-bootstrap/Container";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import YoutubeVideo from "../sub-components/YoutubeVideo";
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from 'react-responsive-carousel';
import FlavorsData from "../sub-components/FlavorsData";

const Flavors = (props) => {
    return (
        <div id="flavors">
            <YoutubeVideo src={"https://www.youtube.com/embed/u0WV5bPSRf8"} />

            <div className="section">
                <Container>
                    {
                        FlavorsData.map(item => {
                            return (
                                <div className="section-inner" key={item.id}>
                                    <Row>
                                        <Col md="6">
                                            <Carousel
                                                autoPlay={false}
                                                interval={5000}
                                                showIndicators={false}
                                                showArrows={false}
                                                showStatus={false}
                                                infiniteLoop={true}>

                                                <img
                                                    className="w-50"
                                                    src={item.bucketImgURL}
                                                    alt={item.title}
                                                />
                                            </Carousel>
                                        </Col>

                                        <Col md="6">
                                            <div className="section-inner">
                                                <img src={item.headingImgURL} alt={item.title} />
                                            </div>

                                            <div className="section-inner">
                                                <h3>{item.description}</h3>
                                            </div>

                                            <div className="section-inner">
                                                <ul>
                                                    {
                                                        item.keyPoints.map(keyPoint => {
                                                            return (
                                                                <li key={item.id}>{keyPoint}</li>
                                                            );
                                                        })
                                                    }
                                                </ul>
                                            </div>

                                            <div className="section-inner">
                                                <p>Ingredients: {item.ingredients}</p>
                                            </div>

                                            <div className="section-inner">
                                                <p>Contains: {item.contains}</p>
                                            </div>

                                            <div className="section-inner">
                                                <p>{item.note}</p>
                                            </div>
                                        </Col>
                                    </Row>
                                </div>
                            );
                        })
                    }


                </Container>
            </div>
        </div>
    );
};


export default Flavors;
