import React from "react";
import Container from "react-bootstrap/Container";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import YoutubeVideo from "../sub-components/YoutubeVideo";

const Flavors = (props) => {
    const [flavorsData, setFlavorsData] = React.useState([]);
    const [carouselData, setCarouselData] = React.useState([]);

    React.useEffect(() => {
        const fetchFlavors = async () => {
            const response = await fetch("/flavors");
            const data = await response.json();

            console.log(data);

            const flavors = [];


            data.forEach(item => {
                const carouselArray = [];

                carouselArray.push({
                    key: 1,
                    src: `images/flavors/${item.img}`,
                    alt: "Flavor Image"
                });

                carouselArray.push({
                    key: 2,
                    src: `images/flavors/${item.gif}`,
                    alt: "Flavor Gif"
                });

                carouselArray.push({
                    key: 3,
                    src: "images/nutri1.png",
                    alt: "Nutrition Value"
                });

                const flavorItem = {
                    key: item._id,
                    name: item.name,
                    description: "Unbelievable on its own, Cream is the foundation of all our flavours. Rich and not too sweet, it tastes amazing with both sweet and savoury pairings. Try it with a spoonful of olive oil or espresso on top…you’ll thank us later.",
                    carousel: carouselArray
                }

                flavors.push(flavorItem);

            });

            console.log(flavors);

            setFlavorsData(flavors);

        };

        fetchFlavors();
    }, []);

    return (
        <div id="flavors">
            <YoutubeVideo src={"https://www.youtube.com/embed/u0WV5bPSRf8"} />

            <div className="section">
                <Container>
                    <Row>
                        <Col md="6">


                        </Col>

                        <Col md="6">
                        {
                                flavorsData.map(flavor => {
                                    return (
                                        <div>
                                            <h1>{flavor.name}</h1>
                                        </div>
                                    );
                                })
                            }
                        </Col>
                    </Row>

                </Container>
            </div>
        </div>
    );
};


export default Flavors;
