import React from "react";
import Container from "react-bootstrap/Container";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import YoutubeVideo from "../sub-components/YoutubeVideo";
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from 'react-responsive-carousel';
import FlavorsData from "../sub-components/FlavorsData";

const Flavors = (props) => {
    return (
        <div id="flavors">
            <YoutubeVideo src={"https://www.youtube.com/embed/u0WV5bPSRf8"} />

            <div className="section">
                <Container>
                    {
                        FlavorsData.map(flavor => {
                            return (
                                <Row key={flavor.key}>
                                    <Col md="5">
                                        <Carousel
                                            autoPlay={true}
                                            interval={5000}
                                            showIndicators={false}
                                            showArrows={false}
                                            showStatus={false}
                                            infiniteLoop={true}>
                                            {
                                                flavor.carousel.map(carouselItem => {
                                                    return (
                                                        <img
                                                            className="w-100"
                                                            src={carouselItem.src}
                                                            alt={carouselItem.alt}
                                                        />
                                                    );
                                                })
                                            }
                                        </Carousel>

                                    </Col>

                                    <Col md="7">
                                        <h1>{flavor.name}</h1>
                                        <h3>{flavor.description}</h3>
                                    </Col>
                                </Row>
                            );
                        })
                    }


                </Container>
            </div>
        </div>
    );
};


export default Flavors;
